import React, {useState } from "react";
import Categories from "./APIs/Categories";

function Form() {
  const [numQst, setNum] = useState(1)
  const [categorie, setCategorie] = useState('')
  const [difficult, setDifficult] = useState('')
  const [type, setType] = useState('')
  const [error,setError] = useState('')
  const [url, setUrl] = useState("https://opentdb.com/api.php?amount=");

  const levels = [
    {
      value : '',
      name : 'Any Difficult'      },
    {
      value : 'easy',
      name : "Easy"      }, 
    {
      value : 'medium',
      name : 'Medium'      
    },
    {
      value : 'difficult',
      name : 'Difficult'
    }
  ]

  const typeS = [
    {
      value : "",
      name : "Any Type"      
    }, 
    {
      value : "multiple",
      name : "Multiple Choice"
    },
    {
      value : "boolean",
      name : "True/False"
    }
  ]

  function getNum(event){
      const {value} = event.target
      setNum(value)
      value > 0 && value < 51 ? setNum(value) : setError('You can choice beetwen 1 and 50 questions')
  }
  function handleDifficult(event) {
    const {value} = event.target
    setDifficult(value)
  }
  function handleCat(event){
    const {value} = event.target
    setCategorie(value)
  }
  function handleType(event) {
    const {value} = event.target
    setType(value)
  }
  function sendInfo(event) {
    event.preventDefault()
    setUrl( prev => {
      if(categorie != ""){
        if(difficult != ""){
          if(type != "") {
            return (
              prev +
              `${numQst}&category=${categorie}&difficulty=${difficult}&type=${type}`
            )
          } else {
            return prev + `${numQst}&category=${categorie}&difficulty=${difficult}`
          }
        } return prev + `${numQst}&category=${categorie}`
        } else {
          return prev + `${numQst}`
        }
      })
    }

    return (
      <div className="form-section">
        <form>
          <label htmlFor="numQst" className="form-item">
            Number of Questions{" "}
          </label>
          <input
            type="number"
            name="numQst"
            className="input form-item"
            id="numQst"
            value={numQst}
            onChange={getNum}
          />
          <Categories function={handleCat}/>
          <label htmlFor="difficult" className="form-item">
            Select Difficult
          </label>
          <select
            id="difficult"
            onChange={handleDifficult}
            className="form-item select"
          >
            {levels.map((level) => {
              return (
                <option key={level.value} value={level.value}>
                  {level.name}
                </option>
              );
            })}
          </select>
          <label htmlFor="type" className="form-item">
            Select Type
          </label>
          
          <select 
            id="type" 
            onChange={handleType} 
            className="form-item select">
              {typeS.map((t) => {
                return (
                  <option value={t.value} key={t.value}>
                    {t.name}
                  </option>
                );
              })}
          </select>

          <button
            onClick={sendInfo}
            className="form-item form-btn"
            type="submit"
          >
            Generate
          </button>
        </form>
        <div className="errors">
          <p>{error}</p>
        </div>
      </div>
    );
}
