import React, {useState } from "react";
import Categories from "./APIs/Categories";

export default function Form() {
    const [numQst, setNum] = useState(0)
    const [categorie, setCategorie] = useState('')
    const [difficult, setDifficult] = useState('')
    const [type, setType] = useState('')
    const [error,setError] = useState('')

    const levels = [
      {
        value : '',
        name : 'Any Difficult'
      },
      {
        value : 'easy',
        name : "Easy"
      }, 
      {
        value : 'medium',
        name : 'Medium'
      },
      {
        value : 'difficult',
        name : 'Difficult'
      }
    ]

    const typeS = [
      {
        value : "",
        name : "Any Type"
      }, 
      {
        value : "multiple",
        name : "Multiple Choice"
      },
      {
        value : "boolean",
        name : "True/False"
      }
    ]

    function getNum(event){
        const {value} = event.target
        setNum(value)
        value > 0 && value < 51 ? setNum(value) : setError('You can choice beetwen 1 and 50 questions')
    }

    function getDifficult(event) {
      const {value} = event.target
      setDifficult(value)
      console.log(difficult)
    }

    return (
      <div className="form-section">
        <form>
          <label for="numQst" className="">Number of Questions </label>
          <input
            type="number"
            name="numQst"
            className="input"
            id="numQst"
            value={numQst}
            onChange={getNum}
          />
          <Categories />
          <label for="difficult">Select Difficult</label> <br />
          <select id="difficult" selected={getDifficult}>
            {levels.map((level) => {
              return (
                <option key={level.value} value={level.value}>
                  {level.name}
                </option>
              );
            })}
          </select>
        </form>
        <div className="errors">
          <p>{error}</p>
        </div>
      </div>
    );
}
