import React, { useState, useEffect } from 'react';
import Categories from './form components/Categories';
import Difficulty from './form components/Difficulty';

export default function Form(props) {
    const [numQst, setNum] = useState(1);
    const [category, setCategory] = useState(9);
    const [difficulty, setDifficulty] = useState('easy');
    const [type, setType] = useState('multiple');

    useEffect( () => {
        const apiUrl = `https://opentdb.com/api.php?amount=${numQst}&category=${category}&difficulty=${difficulty}&type=multiple`;
        console.log(apiUrl);
    },[numQst , category, difficulty, type])

    useEffect(() => {
        //console.log(numQst);
        setNum(numQst)
    })
    useEffect(() => {
        //console.log(category);
        setCategory(category)
    })
    useEffect(() => {
        //console.log(difficulty);
        setDifficulty(difficulty)
    })
    
    const handleNumber = (e) => {
        const {value} = e.target
        return setNum(value)
    }
    const handleCat = (cat) => {
        setCategory(cat)
    }
    const handleDif = (dif) => {
        console.log(dif)
        setDifficulty(dif)
    }

    return (
        <form>
            <label htmlFor='num-qst'>Number of Questions : </label>
            <input 
                id='num-qst'
                type='number'
                value={numQst}
                onChange={handleNumber}
                className='form-item'
            /> <br />
            <Categories onChange={handleCat} />
            <Difficulty onChange={handleDif} />
        </form>
    )
}
