import React, {useState } from "react";
import Categories from "./APIs/Categories";

export default function Form() {
    const [numQst, setNum] = useState(0)
    const [categorie, setCategorie] = useState('')
    const [difficult, setDifficult] = useState('')
    const [type, setType] = useState('')

    const levels = [
      {
        value : '',
        name : 'Any Difficult'
      },
      {
        value : 'easy',
        name : "Easy"
      }, 
      {
        value : 'medium',
        name : 'Medium'
      },
      {
        value : 'difficult',
        name : 'Difficult'
      }
    ]

    const typeS = [
      {
        value : "",
        name : "Any Type"
      }, 
      {
        value : "multiple",
        name : "Multiple Choice"
      },
      {
        value : "",
        
      }
    ]

    function getNum(event){
        const {value} = event.target
        setNum(value)
    }

    function getDifficult(event) {
      const {value} = event.target
      setDifficult(value)
      console.log(difficult)
    }

    return (
      <div className="form-section">
        <form>
          <label>Number of Questions </label>
          <input
            type="number"
            name="numQst"
            className="input"
            min="0"
            max="100"
            value={numQst}
            onChange={getNum}
          />
          <br />

          <label>Select Category</label>
          <Categories /> <br />

          <label>Select Difficult</label>
          <select id="difficult" selected={getDifficult}>
            {levels.map((level) => {
              return (
                <option key={level.value} value={level.value}>
                  {level.name}
                </option>
              );
            })}
          </select> <br />


        </form>
        <div className="choices">
          <p>You had select {numQst} questions o the </p>
        </div>
      </div>
    );
}
