import React, {useState } from "react";
import Categories from "./APIs/Categories";

export default function Form() {
    const [numQst, setNum] = useState(0)
    const [categorie, setCategorie] = useState('')
    const [difficult, setDifficult] = useState('')
    const [type, setType] = useState('')

    const levels = [
      {
        value : '',
        name : 'Any Difficult'
      },
      {
        value : 'easy',
        name : "Easy"
      }, 
      {
        value : 'medium',
        name : 'Medium'
      },
      {
        value : 'difficult',
        name : 'Difficult'
      }
    ]

    function getNum(event){
        const {value} = event.target
        setNum(value)
    }

    function getDifficult(event) {
      const {value} = event.target
      setDifficult(value)
    }

    return (
      <div className="form-section">
        <form>
        <label>Number of questions </label>
          <input
            type="number"
            name="numQst"
            className="input"
            min="1"
            value={numQst}
            onChange={getNum}
          /> <br />
          <label></label>
          <Categories /> <br />
          <select id="difficult">
            {levels.map ( level =>{
              return (
                <option key={level.value} 
                  onChange={getDifficult} 
                  value={level.value}>
                    {level.name}
                </option>
              )
            })}
          </select>
        </form>
        <div className="choices">
            <p>You had select {numQst} questions o the  </p>
        </div>
      </div>
    );
}
