import React, {useState } from "react";
import Categories from "./APIs/Categories";

export default function Form(props) {
  const [numQst, setNum] = useState(1)
  const [categorie, setCategorie] = useState('')
  const [difficult, setDifficult] = useState('')
  const [type, setType] = useState('')
  const [error,setError] = useState('')
  const [url, setUrl] = useState("");

  const levels = [
    {
      value : '',
      name : 'Any Difficult'      },
    {
      value : 'easy',
      name : "Easy"      }, 
    {
      value : 'medium',
      name : 'Medium'      
    },
    {
      value : 'difficult',
      name : 'Difficult'
    }
  ]

  const typeS = [
    {
      value : "",
      name : "Any Type"      
    }, 
    {
      value : "multiple",
      name : "Multiple Choice"
    },
    {
      value : "boolean",
      name : "True/False"
    }
  ]

  function getNum(event){
      const {value} = event.target
      setNum(value)
      value > 0 && value < 51 ? setNum(value) : setError('You can choice beetwen 1 and 50 questions')
  }
  function handleDifficult(event) {
    const {value} = event.target
    setDifficult(value)
  }
  function handleCat(event){
    const {value} = event.target
    setCategorie(value)
  }
  function handleType(event) {
    const {value} = event.target
    setType(value)
  }
  function sendInfo(event) {
    event.preventDefault()
    switch (true) {
      case numQst != "" && categorie != "" && difficult != "" && type != "":
        setUrl(`${numQst}&category=${categorie}&difficulty=${difficult}&type=${type}`)
        break
      case numQst != "" && categorie != "" && difficult != "":
        setUrl(`${numQst}&category=${categorie}&difficulty=${difficult}`)
        break
      case numQst != "" && categorie != "":
        setUrl(`${numQst}&category=${categorie}`)
        break
      case numQst != "" && difficult != "" && type != "":
        setUrl(`${numQst}&difficulty=${difficult}&type=${type}`)
        break
      case numQst != "" && difficult != "":
        setUrl(`${numQst}&difficulty=${difficult}`)
        break
      case numQst != "" && categorie != "" && type != "":
        setUrl(`${numQst}&category=${categorie}&type=${type}`)
        break
      case numQst != "" && type != "":
        setUrl(`${numQst}&type=${type}`)
        break
      default:
        setUrl(`${numQst}`)
        break
    }
    
  }
  function getUrl(event){
    event.preventDefault();
    props.onSubmit
  }

  return (
    <div className="form-section">
      <form onSubmit={getUrl}>
        <label htmlFor="numQst" className="form-item">
          Number of Questions{" "}
        </label>
        <input
          type="number"
          name="numQst"
          className="input form-item"
          id="numQst"
          value={numQst}
          onChange={getNum}
        />
        <Categories function={handleCat}/>
        <label htmlFor="difficult" className="form-item">
          Select Difficult
        </label>
        <select
          id="difficult"
          onChange={handleDifficult}
          className="form-item select"
        >
          {levels.map((level) => {
            return (
              <option key={level.value} value={level.value}>
                {level.name}
              </option>
            );
          })}
        </select>
        <label htmlFor="type" className="form-item">
          Select Type
        </label>
        
        <select 
          id="type" 
          onChange={handleType} 
          className="form-item select">
            {typeS.map((t) => {
              return (
                <option value={t.value} key={t.value}>
                  {t.name}
                </option>
              );
            })}
        </select>

        <button
          onClick={sendInfo}
          className="form-item form-btn"
          type="submit"
        >
          Generate
        </button>
      </form>
      <div className="errors">
        <p>{error}</p>
      </div>
    </div>
    );
}

