import React, {useState, useEffect} from 'react';
import he from "he" ;

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

export default function Quiz(props) {
    const {results} = props.questions
    const [rightAnwsers, setAnswer] = useState([]);
    const [choice , setChoice] = useState()
    const [decodedResults, setDecodedResults] = useState([]);
    const [score, setScore] = useState(0)

    useEffect(() => {
      const decodedData = results.map((question) => {
        const decodedQuestion = {
          ...question,
          question: he.decode(question.question),
          correct_answer: he.decode(question.correct_answer),
          incorrect_answers: question.incorrect_answers.map((answer) =>
            he.decode(answer)
          ),
        };
        setAnswer(prev => {
          return [
            ...prev,
            {
              question: decodedQuestion.question,
              answer: decodedQuestion.correct_answer,
            },
          ];
        });
        return decodedQuestion;
      });

      setDecodedResults(decodedData);
    }, [results]);
    const handleAnswer = e => {
      const {value} = e.target
      setAnswer()
    }
    const checkAnswer = (e) => {
      e.preventDefault()
      console.log(rightAnwsers)
    }

    return (
        <div className='quiz-item'>
            {decodedResults.map((q ,index) => {
                const { correct_answer, incorrect_answers, question } = q;
                const options = shuffleArray([correct_answer,...incorrect_answers])

                return (
                  <div key={index}>
                    <h2>{question}</h2>
                    <form className='quiz-form'>
                        {options.map(option => {
                            return(
                                <div key={option}>
                                    <input 
                                        id={option}
                                        value={option}
                                        name={index}
                                        type='radio'
                                        className='quiz-form-item'
                                        onClick={handleAnswer}
                                    />
                                    <label htmlFor={option}>{option}</label>
                                </div>
                            )
                        })}
                        <button onClick={checkAnswer} type='submit' name={index} >Confirm</button>
                    </form>
                  </div>
                );
            })}        
        </div>
    )
}
