import React, {useState, useEffect} from 'react';
import he from "he" ;

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
  return array;
}

export default function Quiz(props) {
    const {results} = props.questions
    const [rightAnwsers, setAnswer] = useState([]);
    const [currentAnswer, setCA] = useState({ question : "" , answer : ""})
    const [decodedResults, setDecodedResults] = useState([]);
     const [shuffledOptions, setShuffledOptions] = useState([])
    const [score, setScore] = useState(0)

    useEffect(() => {
      const decodedData = results.map((question) => {
        const decodedQuestion = {
          ...question,
          question: he.decode(question.question),
          correct_answer: he.decode(question.correct_answer),
          incorrect_answers: question.incorrect_answers.map((answer) =>
            he.decode(answer)
          ),
        };
        setAnswer(prev => {
          return [
            ...prev,
            {
              question: decodedQuestion.question,
              answer: decodedQuestion.correct_answer,
            },
          ];
        });
        return decodedQuestion;
      });

      setDecodedResults(decodedData);
      const allOptions = decodedData.map((q) =>
        shuffleArray([q.correct_answer, ...q.incorrect_answers])
      );
      setShuffledOptions(allOptions);
    }, [results]);
    const handleChoice = (qst, ans) => {
      console.log({
        question: qst,
        answer: ans,
      });
      setCA({
        question: qst,
        answer: ans,
      });
    };
    const handleSubmit = (e) => {
      e.preventDefault();
      decodedResults.forEach((q) => {
        const { question, correct_answer } = q;
        if (currentAnswer.question === question) {
          if (currentAnswer.answer === correct_answer) {
            setScore((prevScore) => prevScore + 1); 
          }
        }
      });
      console.log(currentAnswer);
      console.log(score)
    };

    return (
        <div className='quiz-item'>
          <p>Score :{score}</p>
            {decodedResults.map((q ,index) => {
                const { correct_answer, incorrect_answers, question } = q;
                const options = shuffledOptions[index]

                return (
                  <div key={index}>
                    <h2>{question}</h2>
                    <form className='quiz-form'>
                        {options.map(option => {
                            return (
                              <div key={option}>
                                <input
                                  id={option}
                                  value={option}
                                  name={index}
                                  type="radio"
                                  className="quiz-form-item"
                                  checked={currentAnswer.question === question && currentAnswer.answer === option}
                                  onChange={() => handleChoice(question,option)}
                                />
                                <label
                                  htmlFor={option}
                                  onClick={() => {
                                    setCA({
                                      question: question,
                                      answer: option,
                                    });
                                  }}
                                >
                                  {option}
                                </label>
                              </div>
                            );
                        })}
                        <button
                         onClick={handleSubmit} 
                          type='submit' 
                          >
                            Confirm
                          </button>
                    </form>
                  </div>
                );
            })}        
        </div>
    )
}
