import React, {useState , useEffect} from "react";

export default function Form() {
    const [numQst, setNum] = useState(0)

    function getNum(event){
        const {value} = event.target
        setNum(value)
    }
    useEffect(() => {
        
    })
    return (
        <form className="form">
            <input 
                type="number" 
                placeholder="Number of question" 
                name="numQst" 
                className="input"
                value={numQst}
                onChange={getNum}
            />
            <p>Latest value : {numQst} </p>
        </form>
    )
}
