import React, { useState, useEffect } from 'react';
import Categories from './Categories';

export default function Form(props) {
    const [numQst, setNum] = useState(1);
    const [category, setCategory] = useState(9);
    const [difficulty, setDifficulty] = useState('easy');
    const [type, setType] = useState('multiple');
    const [url, setUrl] = useState(
      `https://opentdb.com/api.php?amount=${numQst}&category=${category}&difficulty=${difficulty}&type=${type}`
    );

    useEffect(() => {
        setNum(numQst)
    })
    useEffect(() => {
        
        setCategory(category)
    })
    
    const handleNumber = (e) => {
        const {value} = e.target
        return setNum(value)
    }
    const handleCat = (cat) => {
        return setCategory(cat)
    }

    return (
        <form>
            <label htmlFor='num-qst'>Number of Questions : </label>
            <input 
                id='num-qst'
                type='number'
                value={numQst}
                onChange={handleNumber}
                className='form-item'
            /> <br />
            <Categories onChange={handleCat} />
        </form>
    )
}
