import React, { useState, useEffect } from 'react';
import Categories from './form components/Categories';
import Difficulty from './form components/Difficulty';
import Type from './form components/Type';
import Number from './form components/NumberQst';

export default function Form(props) {
    const [numQst, setNum] = useState(1);
    const [category, setCategory] = useState(9);
    const [difficulty, setDifficulty] = useState('easy');
    const [type, setType] = useState('multiple');


    useEffect( () => {
        const apiUrl = `https://opentdb.com/api.php?amount=${numQst}&category=${category}&difficulty=${difficulty}&type=${type}`;
        console.log(apiUrl);
    },[numQst , category, difficulty, type])

    useEffect(() => {
        console.log(numQst);
        setNum(numQst)
    })
    useEffect(() => {
        console.log(category);
        setCategory(category)
    })
    useEffect(() => {
        console.log(difficulty);
        setDifficulty(difficulty)
    })
    useEffect(()=>{
        console.log(type)
        setType(type)
    })
    
    const handleNumber = (num) => {
        return setNum(value)
    }
    const handleCat = (cat) => {
        setCategory(cat)
    }
    const handleDif = (dif) => {
        setDifficulty(dif)
    }
    const handleType = (type) => {
        setType(type)
    }

    return (
        <form>
            <Number onChange={handleNumber}/>
            <Categories onChange={handleCat} />
            <Difficulty onChange={handleDif} />
            <Type onChange={handleType}/>
        </form>
    )
}
